import React from 'react'
import './App.css'
import Pagination from 'react-bootstrap/Pagination'

// const HandleClick = () => {

// }

const paginate = (totalMovies, currentPage, moviePerPage, changePage) => {
    const items = []
    if (!totalMovies) {
        return items
    }
    const totalPage = Math.ceil(totalMovies / moviePerPage)
    const pagelist = []
    for (let i = Math.max(1, currentPage - 2); i <= Math.min(totalPage, currentPage + 2); i++) {
        pagelist.push(i)
    }

    items.push(<Pagination.First key={0} onClick={() => changePage(1)} />)
    items.push(<Pagination.Prev key={-1} disabled={currentPage === 1} onClick={() => {
        if (currentPage === 1) {
            return false;
        }
        changePage(currentPage - 1)
    }} />)
    pagelist.forEach(element => {
        items.push(<Pagination.Item key={element} active={currentPage === element} onClick={() => changePage(element)}>
            {element}
        </Pagination.Item>)
    });
    items.push(<Pagination.Next disabled={currentPage === totalPage} onClick={() => {
        if (totalPage === currentPage) {
            return false;
        }
        changePage(currentPage + 1)
    }} />)
    items.push(<Pagination.Last onClick={() => changePage(totalPage)} />)

    return (
        <Pagination className="justify-content-center mb-4" bsPrefix='pagination'>
            {items}
        </Pagination>
    )
}


function List({ list, moviePerPage, totalMovies, currentPage, changePage }) {
    const postlist = list.length ? (
        list.map(movie => {
            return (
                <div className='col-6 col-sm-4 col-md-3 col-xl-2 mb-3 mt-3 ' key={movie.movie_id}>
                    <div className='box '>
                        <a href={movie.thumbnail_link}>
                            <img className=' mb-3' src={movie.thumbnail_link} alt={movie.movie_id}
                                style={{ borderRadius: 10, width: 150, height: 220 }} />
                        </a>
                    </div>
                </div>
            )
        })
    ) : (
            <div className="text-center">
                Loading
            </div>
        )


    return (
        <div className='container-fluid body-cont' style={{ paddingTop: 5, paddingBottom: 15 }}>
            <main>
                <div className='container'>
                    <button type='button' className='btn btn-outline-primary my-3'>
                        Catalogue {' '}
                        <i className='fa fa-chevron-right' style={{ marginTop: 4 }}>
                        </i>
                    </button>
                    <div className='row'>
                        {postlist}
                    </div>
                </div>
                {paginate(totalMovies, currentPage, moviePerPage, changePage)}
            </main>
        </div>
    )
}

export default List;
