import React from "react";
import { createStyles, makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import "./App.css";
// import Box from '@material-ui/core/Box';

const useStyles = makeStyles((theme) =>
  createStyles({
    formControl: {
      margin: theme.spacing(3),
      minWidth: 120
    },
    formControlo: {
      margin: theme.spacing(3),
      marginTop: theme.spacing(4),
      minWidth: 120
    }
  })
);

const GenreList = ["All",
  "Crime",
  "Animation",
  "Documentary",
  "Romance",
  "History",
  "Mystery",
  "Fantasy",
  "Science Fiction",
  "Horror",
  "Drama",
  "Action",
  "Thriller",
  "Comedy",
  "Adventure",
  "War",
  "Family"
].map(gen => {
  return (
    <MenuItem value={gen} key={gen}>{gen}</MenuItem>
  )
})

export default function SimpleSelect({updateParams}) {
  const classes = useStyles();
  console.log(this.props)
  // const [age, setAge] = React.useState("");
  const [genre, setGenre] = React.useState("All");
  const [sortBy, setSortBy] = React.useState("vote_average DESC");
  const handleChange = (e) => {
    if (e.target.name === "Genre") {
      setGenre(e.target.value)
    } else {
      setSortBy(e.target.value)
    }

    // setAge(e.target.value);
  };
  const handlesubmit = (e) => {
    e.preventDefault()
    updateParams(e.target[0].value, e.target[1].value, e.target[2].value)
  }

  return (
    <div className="container" >
      <div className="card">
        <div className="container">
          <div className="searchbar-container">

            <form onSubmit = {handlesubmit}>
            <div className = "row">
            <div className = "col-12 col-sm-6 col-md-3">
            <FormControl className={classes.formControl} mx="auto" name = "text">
              <TextField id="standard-basic" label="Search" onSubmit={handlesubmit} />
            </FormControl>
            </div>
            <div className = "col-12 col-sm-6 col-md-3">
            <FormControl className={classes.formControl} mx="auto">
              <InputLabel id="demo-simple-select-label">Genre</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={genre}
                onChange={handleChange}
                name="Genre"
              >
                {GenreList}
              </Select>
            </FormControl>
            </div>
            <div className = "col-12 col-sm-6 col-md-3">
            <FormControl className={classes.formControl}>
              <InputLabel id="demo-simple-select-label">SortBy</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={sortBy}
                onChange={handleChange}
                name="SortBy"
              >
                <MenuItem value={"vote_average DESC"}>Rating Decreasing</MenuItem>
                <MenuItem value={"vote_average ASC"}>Rating Increasing</MenuItem>
                <MenuItem value={"vote_count DESC"}>Popularity Increasing</MenuItem>
                <MenuItem value={"vote_count ASC"}>Popularity Decreasing</MenuItem>
                <MenuItem value={"movie_name DESC"}>A to Z</MenuItem>
                <MenuItem value={"movie_name ASC"}>Z to A</MenuItem>
              </Select>
            </FormControl>
            </div>
            <div className = "col-12 col-sm-6 col-md-3">
            <FormControl className={classes.formControlo}>
              <Button type="submit" variant="contained" color="primary">
                Primary
              </Button>
            </FormControl>
            </div>

            </div>
            </form>
            </div>
        </div>
      </div>
    </div>
  );
}
