import React from 'react'
import './App.css'
import Col from 'react-bootstrap/Col'
import Container from 'react-bootstrap/Container'
import CatalogueButton from './CatalogueButton'

const process = (imglink) => {
    console.log(imglink)
    const arr = imglink.split("/")
    console.log(arr)

    return imglink
}


function List({ list, catalogueHidden}) {
    const postlist = list.length ? (
        list.map(movie => {
            return (
                <Col xs={12} sm={6} md={3} large={2} xl={2} className="mb-3 mt-3 justify-content-center text-center" key = {movie.movie_id}>

                    <div className='box '>
                        <a href={process(movie.thumbnail_link)}>
                            <img className=' mb-3' src={movie.thumbnail_link} alt={movie.movie_id}
                                style={{ borderRadius: 10, width: 130, height: 200 }} />
                        </a>
                    </div>
                </Col>
            )
        })
    ) : (
            <div className="text-center">
                Loading
            </div>
        )


    return (
        <div className='container body-cont' style={{ paddingTop: 5, paddingBottom: 15 }}>
            <main>
                <Container>
                    <CatalogueButton hidden = {catalogueHidden} /> 
                    <div className='row'>
                        {postlist}
                    </div>
                </Container>

            </main>
        </div>
    )
}

export default List;
