import React, { Component } from 'react';
import './App.css';
import './index.css';
import List from './List'
import Navbartwo from './Navbartwo'
import Footer from './Footer'
import axios from 'axios'
import Paginate from './Paginate'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: 1,
      moviesPerPage: 24,
      result: [],
      totalMovies : 0,
    }
  }

  componentDidMount() {
    axios.get('http://localhost:8080/hulu?language=en&sortby=vote_average DESC&limit=100&offset=1')
      .then(res => {
        // console.log(res.data)
        this.setState({
          result : res.data,
          totalMovies : res.data.length,
        }
        )
      }
      )
  }

  changePage = (pageno) => {
    this.setState({
      currentPage : pageno,
    })
  }

  render() {
    const result = this.state.result.slice(( this.state.currentPage - 1) * this.state.moviesPerPage,this.state.currentPage * this.state.moviesPerPage);
    return (
      <div style={{ backgroundColor: "#d2d2d275" }}>
        <Navbartwo />
        <List list = {result} moviePerPage = {this.state.moviesPerPage} totalMovies = {this.state.totalMovies} currentPage = {this.state.currentPage} changePage = {this.changePage}/>
        <Paginate totalMovies = {totalMovies} currentPage = {currentPage}  moviePerPage = {moviePerPage} changePage = {changePage} />
        <Footer />
      </div >
    );
  }
}

export default App;
