import React, { Component } from 'react';
import './App.css';
import './index.css';
import List from './List'
import axios from 'axios'
import Paginate from './Paginate'
import SimpleSelect from './Filter'

const seperate = (str) => {
    const obj = {}
    if(str === ""){
        return obj
    }
    const strArr = str.slice(1).split("&")
    strArr.map( (param) => {
        obj[param.split("=")[0]] = param.split("=")[1] 
    })
    return obj
}

class SearchPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            currentPage: 1,
            moviesPerPage: 24,
            result: [],
            totalMovies: 0,
            search: this.udpateState("search"),
            genre: this.udpateState("genre"),
            sortBy: "vote_average DESC"
        }
    }

    getquery() {
        const query = [];
        if (this.state.search !== "") {
            query.push('search=' + this.state.search)
        }
        if (this.state.genre !== "All") {
            query.push('genre=' + this.state.genre)
        }
        query.push('sortby=' + this.state.sortBy)
        return 'http://localhost:8080/movie/catalogue?' + query.join('&')
    }


    getMovies() {
        axios.get(this.getquery())
            .then(res => {
                if (res.data !== null) {
                    this.setState({
                        result: res.data,
                        totalMovies: res.data.length,
                    }
                    )
                } else {
                    this.setState({
                        result: [],
                        totalMovies: 0,
                    }
                    )
                }
            }
            )
    }

    udpateState(query) {
        const queryParams = this.props.location.search
        if (queryParams !== "") {
            const queriesObj = seperate(queryParams)
            for(let key in queriesObj){
                if(key === query){
                    return queriesObj[query]
                }
            }
        }
        if (query === "genre") {
            return "All"
        }
        return ""

    }

    componentDidMount() {
        this.getMovies()
    }


    newUpdateParam = (key, param) => {
        this.setState({
            [key]: param,
        },
            () => this.getMovies()
        )
    }

    updateParams = (search, genre, sortBy) => {
        this.setState({
            search: search,
            genre: genre,
            sortBy: sortBy,
        },
            () => this.getMovies()
        )
    }


    changePage = (pageno) => {
        this.setState({
            currentPage: pageno,
        })
    }

    render() {


        const result = this.state.result.slice((this.state.currentPage - 1) * this.state.moviesPerPage, this.state.currentPage * this.state.moviesPerPage);

        const initparams = {
            search: this.state.search,
            genre: this.state.genre,
        }

        return (
            <div style={{ backgroundColor: "#d2d2d275" }}>

                <SimpleSelect updateParams={this.updateParams} initparams={initparams} />
                <List list={result} catalogueHidden={true} />
                <Paginate totalMovies={this.state.totalMovies} currentPage={this.state.currentPage} moviePerPage={this.state.moviesPerPage} changePage={this.changePage} />

            </div >
        );
    }
}

export default SearchPage;
