import React from "react";
import { createStyles, makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import "./App.css";
// import Box from '@material-ui/core/Box';

const useStyles = makeStyles((theme) =>
  createStyles({
    formControl: {
      margin: theme.spacing(3),
      minWidth: 120
    },
    formControlo: {
      margin: theme.spacing(3),
      marginTop: theme.spacing(4),
      minWidth: 120
    }
  })
);

const GenreList = ["Crime",
  "Animation",
  "Documentary",
  "Romance",
  "History",
  "Mystery",
  "Fantasy",
  "Science Fiction",
  "Horror",
  "Drama",
  "Action",
  "Thriller",
  "Comedy",
  "Adventure",
  "War",
  "Family"
].map(gen => {
  return(
    <MenuItem value={gen}>{gen}</MenuItem>
  )
})

export default function SimpleSelect() {
  const classes = useStyles();
  // const [age, setAge] = React.useState("");
  const [genre, setGenre] = React.useState("");
  const [sortBy, setSortBy] = React.useState("vote_average DESC");
  const handleChange = (e) => {
    if(e.target.name === "Genre"){
      setGenre(e.target.value)
    } else {
      setSortBy(e.target.value)
    }
    // setAge(e.target.value);
  };

  return (
    <div className="container" >
      <div className="card">
        <div className="container">
          <div className="searchbar-container">

            <FormControl className={classes.formControl} mx = "auto">
              <TextField id="standard-basic" label="Search" />
            </FormControl>
            <FormControl className={classes.formControl} mx = "auto">
              <InputLabel id="demo-simple-select-label">Genre</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={genre}
                onChange={handleChange}
                name = "Genre"
              >
                <MenuItem>
                  <em>All</em>
                </MenuItem>

                <MenuItem value={"Action"}>Action</MenuItem>
                <MenuItem value={"Horror"}>Horror</MenuItem>
                <MenuItem value={"Triler"}>Thriler</MenuItem>
              </Select>
            </FormControl>

            <FormControl className={classes.formControl}>
              <InputLabel id="demo-simple-select-label">SortBy</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={sortBy}
                onChange={handleChange}
                name = "SortBy"
              >
                <MenuItem value={"vote_average DESC"}>Rating Decreasing</MenuItem>
                <MenuItem value={"vote_average ASEC"}>Rating Increasing</MenuItem>
                <MenuItem value={"popularity DESC"}>Popularity Increasing</MenuItem>
                <MenuItem value={"popularity ASEC"}>Popularity Decreasing</MenuItem>
                <MenuItem value={"movie_title DESC"}>A to Z</MenuItem>
                <MenuItem value={"movie_title ASEC"}>Z to A</MenuItem>
              </Select>
            </FormControl>
            <FormControl className={classes.formControlo}>
              <Button variant="contained" color="primary">
                Primary
          </Button>
            </FormControl>
          </div>
        </div>
      </div>
    </div>
  );
}
