import React from 'react'
import './App.css'
// import data from './list.json'

function List({ list, moviePerPage, totalMovies, currentPage, changePage }) {
    // console.log(list)

    const postlist = list.length ? (
        list.map(movie => {
            return (
                <div className='col-6 col-sm-4 col-md-3 col-xl-2 mb-3 mt-3 ' key={movie.movie_id}>
                    <div className='box '>
                        <a href={movie.thumbnail_link}>
                            <img className=' mb-3' src={movie.thumbnail_link} alt={movie.movie_id}
                                style={{ borderRadius: 10, width: 150, height: 220 }} />
                        </a>
                    </div>
                </div>
            )
        })
    ) : (
            <div className="text-center">
                Loading
            </div>
        )

    const pagelist = []
    for (let i = 0; i <= Math.ceil(totalMovies / moviePerPage) + 1; i++) {
        pagelist.push(i)
    }

    const pagination = pagelist.length ? (
        console.log('b'),
        pagelist.map(pageno => {
            let stylecss = 'page-item'
            let contentPagination = pageno.toString()
            let disprev = false
            let disnext = false
            if (pageno === 0) {
                contentPagination = 'prev'
                if (currentPage === 1) {
                    stylecss += ' disabled'
                    disprev =true
                }
            } else if (pageno === Math.ceil(totalMovies / moviePerPage) + 1) {
                contentPagination = 'next'
                if (currentPage === Math.ceil(totalMovies / moviePerPage)) {
                    stylecss += ' disabled'
                    disnext = true
                }
            } else if (pageno === currentPage) {
                stylecss += ' active'
            }
            return (<li className={stylecss} key={pageno}
                onClick={() => {
                    if (disprev || disnext) {
                        return false;
                    } else if (contentPagination === 'next') {
                        changePage(currentPage + 1)
                    }
                    else if(pageno === 0)   {
                        changePage(currentPage -1)
                    }
                    else{
                        changePage(pageno)
                    }
                }}>
                <a className="page-link" href="#prev">
                    {contentPagination}
                </a>
            </li>)
        }
        )
    ) : (
            null
        )
    console.log(pagination)


    return (
        // <h1>pawan</h1>
        <div className='container-fluid body-cont' style={{ paddingTop: 5, paddingBottom: 15 }}>
            <main>
                <div className='container'>
                    <button type='button' className='btn btn-outline-primary my-3'>
                        Catalogue {' '}
                        <i className='fa fa-chevron-right' style={{ marginTop: 4 }}>
                        </i>
                    </button>
                    <div className='row'>

                        {postlist}
                    </div>
                </div>
                <ul className="pagination justify-content-center mb-4">
                    {pagination}
                    {    // <li className="page-item disabled"><a className="page-link" href="#prev">Previous</a></li>
                        // <li className="page-item active" onClick={() => console.log('click!!')}><a className="page-link" href="#pawan">1</a></li>
                        // <li className="page-item"><a className="page-link" href="#pawan">2</a></li>
                        // <li className="page-item"><a className="page-link" href="#pawan">3</a></li>
                        // <li className="page-item"><a className="page-link" href="#pawan">Next</a></li>
                    }</ul>
            </main>
        </div>
    )
}

export default List;
