import React, { Component } from 'react';
import './App.css';
import './index.css';
import List from './List'
import axios from 'axios'
import Paginate from './Paginate'
import SimpleSelect from './Filter'


class SearchPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            currentPage: 1,
            moviesPerPage: 24,
            result: [],
            totalMovies: 0,
            search: "",
            genre: "All",
            sortBy: "vote_average DESC"
        }
    }

    getquery() {
        const query = [];
        if (this.state.search !== "") {
            query.push('search=' + this.state.search)
        }
        if (this.state.genre !== "All") {
            query.push('genre=' + this.state.genre)
        }
        query.push('sortby=' + this.state.sortBy)
        // 'http://localhost:8080/movie/catalogue?' + 
        // console.log(query)
        return 'http://localhost:8080/movie/catalogue?' + query.join('&')
    }


    getMovies() {
        axios.get(this.getquery())
            .then(res => {
                console.log(res)
                console.log("ad")
                if (res.data !== null) {
                    this.setState({
                        result: res.data,
                        totalMovies: res.data.length,
                    }
                    )
                } else {
                    this.setState({
                        result: [],
                        totalMovies: 0,
                    }
                    )
                }

            }
            )
    }

    componentDidMount() {
        const queryParams = this.props.location.search
        if (queryParams !== "") {
            // for now we are only considering one qyery param
            const params = queryParams.slice(1).split("&")
            console.log(params[0].charAt(0))
            if (params[0].charAt(0) === 's') {
                const searchParam = params[0].split("=")[1]
                this.updateParams(searchParam, "All", "vote_average DESC")
            } else if (params[0].charAt(0) === 'g') {
                const genreParam = params[0].split("=")[1]
                console.log(genreParam, "a")
                this.updateParams("", genreParam, "vote_average DESC")
            }

        } else {
            this.getMovies()
        }
    }


    updateParams = (search, genre, sortBy) => {
        this.setState({
            search: search,
            genre: genre,
            sortBy: sortBy,
        },
            () => this.getMovies()
        )

    }


    changePage = (pageno) => {
        this.setState({
            currentPage: pageno,
        })
    }






    render() {
        const result = this.state.result.slice((this.state.currentPage - 1) * this.state.moviesPerPage, this.state.currentPage * this.state.moviesPerPage);

        const initparams = {
            state: this.state.search,
            genre: this.state.genre,
            sortby: this.state.sortBy
        }

        return (
            <div style={{ backgroundColor: "#d2d2d275" }}>

                <SimpleSelect updateParams={this.updateParams} initparams={initparams} />
                <List list={result} catalogueHidden={true} />
                <Paginate totalMovies={this.state.totalMovies} currentPage={this.state.currentPage} moviePerPage={this.state.moviesPerPage} changePage={this.changePage} />

            </div >
        );
    }
}

export default SearchPage;
