import React from 'react'
import './App.css'
import Col from 'react-bootstrap/Col'
import Container from 'react-bootstrap/Container'
import CatalogueButton from './CatalogueButton'
import DNE from './Poster_not_available.jpg'

const process = (imglink) => {
    // console.log(imglink)
    const arr = imglink.split("/")
    // console.log(arr)
    if(arr.length !== 7){
        return DNE
    }
    arr[5] = "w154"
    imglink =  arr.join('/')
    // console.log(imglink)
    // imglink = imglink.slice(0,-3) + 'png'
    return imglink
}


function List({ list, catalogueHidden}) {
    const postlist = list.length ? (
        list.map(movie => {
            return (
                <Col xs={12} sm={6} md={3} large={2} xl={2} className="mb-3 mt-3 justify-content-center text-center" key = {movie.movie_id}>

                    <div className='box '>
                        <a href={process(movie.thumbnail_link)}>
                            <img className=' mb-3' src={process(movie.thumbnail_link)} loading = "lazy" alt={movie.movie_id}
                                style={{ borderRadius: 10, width: 130, height: 194 }} />
                        </a>
                    </div>
                </Col>
            )
        })
    ) : (
            <div >
            <Col className="mb-3 mt-3 justify-content-center text-center" key = {-1}>No movie </Col>
            </div>
        )


    return (
        <div className='container body-cont' style={{ paddingTop: 5, paddingBottom: 15 }}>
            <main>
                <Container>
                    <CatalogueButton hidden = {catalogueHidden} /> 
                    <div className='row'>
                        {postlist}
                    </div>
                </Container>

            </main>
        </div>
    )
}

export default List;
