import React, { Component } from 'react';
import './App.css';
import './index.css';
import List from './List'
import Navbartwo from './Navbartwo'
import Footer from './Footer'
import axios from 'axios'
import Paginate from './Paginate'
import SimpleSelect from './Filter'
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: 1,
      moviesPerPage: 24,
      result: [],
      totalMovies : 0,
      search : "",
      genre : "All",
      sortBy : "vote_average DESC"
    }
  }
  getquery(){
    const query = [];
    if(this.state.search !== ""){
      query.push('search=' + this.state.search)
    }
    if(this.state.genre !== "All"){
      query.push('genre=' + this.state.genre)
    }
    query.push('sortby=' + this.state.sortBy)
    console.log(query)
    return query.join('&')
  }

  componentDidMount() {
    axios.get('http://localhost:8080/movie/catalogue?' + this.getquery() )
      .then(res => {
        // console.log(res.data)
        this.setState({
          result : res.data,
          totalMovies : res.data.length,
        }
        )
      }
      )
  }

  changePage = (pageno) => {
    this.setState({
      currentPage : pageno,
    })
  }

  searchsubmit = (e) => {
    e.preventDefault()
    console.log(e.target[0].value)
  }

  render() {
    const result = this.state.result.slice(( this.state.currentPage - 1) * this.state.moviesPerPage,this.state.currentPage * this.state.moviesPerPage);
    return (
      <div style={{ backgroundColor: "#d2d2d275" }}>

        <Navbartwo searchsubmit = {this.searchsubmit} catalogueHidden = {false}/>
        <SimpleSelect />
        <List list = {result} catalogueHidden = {false}/>
        <Paginate totalMovies = {this.state.totalMovies} currentPage = {this.state.currentPage}  moviePerPage = {this.state.moviesPerPage} changePage = {this.changePage} />
        <Footer />
      </div >
    );
  }
}

export default App;
